$(function() {
    $(".dialog-box .title .close").click(function() {
        $(this).parents(".dialog").hide();
    });
    $(".checkbox").click(function(a) {
        var b = $(this).find("input").val();
        a.stopPropagation();
        $(this).find("i").toggleClass("checked").toggleClass("check");
        $(this).find("input").val(b == 0 ? 1 : 0);
    });
    $(".selectGroup").click(function(a) {
        var b = $(a.target);
        if (b.is(".select") || b.is("label") || b.is("em")) {
            b = $(this).find(".select");
            $(".selectGroup .select-items").not(b.next()).hide();
            b.next().toggle();
            b.find("em").toggleClass("bottomdirection").toggleClass("topdirection");
        } else if (b.is("li")) {
            b.parent().find("li.active").removeClass("active");
            b.addClass("active");
            b.parents(".selectGroup").find(".select").attr("date-value", b.attr("date-value")).find(".text").text(b.text());
            b.parent().hide();
            $(this).find("em").toggleClass("bottomdirection").toggleClass("topdirection");
        }
    });
    $(document).click(function(a) {
        var b = $(a.target).closest(".selectGroup");
        if (!b.size()) {
            $(".selectGroup .select-items").hide();
            $(".selectGroup .select >em").removeClass().addClass("bottomdirection");
        } else {
            $(".selectGroup").not(b).find(".select-items").hide();
        }
    });
    $(".input-group input").one("focus", function(a) {
        $(this).parent().find(".placeholder").hide();
    });
    $("#header-search-input").one("focus", function(a) {
        $(this).parent().find(".placeholder").hide();
    });
    $("#aht-search-input").one("focus", function(a) {
        $(this).parent().find(".placeholder").hide();
    });
    $("#map-search-input").one("focus", function(a) {
        $(this).parent().find(".placeholder").hide();
    });
    $(".input-group input,#header-search-input,#aht-search-input").load(function() {
        if ($(this).val != "" || $(this).val != null) {
            $(this).parent().find(".placeholder").hide();
        }
    });
    var a = $(window).innerHeight() - $("#footer").innerHeight();
    $("#content").innerHeight() <= a ? $("#content").innerHeight(a) : $("#content").css("height", "auto");
});

function eleCenter(a) {
    var b = $(window).width(), c = $(window).height(), d = $(a), e = d.width(), f = d.height(), g = c > f ? (c - f) / 2 : 0, h = b > e ? (b - e) / 2 : 0;
    d.css({
        top: g,
        left: h
    });
}